import styled from "styled-components";

const colors = {
    darkBlue: '#18202E',
    white: '#fff',
    red: '#FF6149',
    yellow: '#FFE75E',
    blue: '#296FFF'
}

const MainScreen = styled.div`
    display: flex;
    flex-direction: column;
`;

const FirstScreen = styled.div`
    background: ${colors.darkBlue};
    padding-left: 30px;
    padding: 0 45px 60px 45px;
    display: grid;
    grid-template-columns: 1fr 10%;
    height: 100%;
`;

const Presentation = styled.div`
    display: flex;
    flex-direction: column;
    padding-left: 15px;

    h2 {
        margin-top: 5%;
        font-size: 60px;
    }
`;

const Menu = styled.div`
    padding: 25px;
    grid-column: span 2;
    display: flex;
    flex-direction: row-reverse;
`;

const OptionMenu = styled.a`
    color: ${colors.white};
    padding-left: 20px;
    text-decoration: none;

    &:hover{
        color: ${colors.yellow};
    }
`;

const ButtonAnimate = styled.button`
    background: ${colors.red};
    width: 200px;
    text-decoration: none;
    border: none;
    padding: 15px;
    margin-top: 60px;
    cursor: pointer;

    &:hover{
        background: ${colors.blue};
    }
`;

const Networks = styled.div`
    display: flex;
    flex-direction: column-reverse;
    align-items: center;
`;

const AboutMeDiv = styled.div`
    background: orange;
    height: 400px;
`;

const ExperienceDiv = styled.div`
    background: yellow;
    height: 400px;
`;

const ContactDiv = styled.div`
    background: blue;
    height: 400px;
`;



export { MainScreen, FirstScreen, ButtonAnimate, Menu, OptionMenu, Presentation, Networks,
            AboutMeDiv, ExperienceDiv, ContactDiv }
