import styled from "styled-components";

const colors = {
    darkBlue: '#18202E',
    red: '#FF6149',
    yellow: '#FFE75E',
    blue: '#296FFF'
}

const MainScreen = styled.div`
    display: flex;
    flex-direction: column;
`;

const FirstScreen = styled.div`
    background: ${colors.darkBlue};
    padding: 30px;

    display: grid;
    grid-template-columns: 1fr 10%;
`;

const Presentation = styled.div`
    display: flex;
    flex-direction: column;
    padding: 30px;
`;

const Menu = styled.div`
    background: pink;
    padding: 30px;

    grid-column: span 2;
    display: flex;
    flex-direction: row-reverse;
`;

const OptionMenu = styled.a`
    padding-left: 20px;
    text-decoration: none;
`;

const ButtonAnimate = styled.button`
    background: ${colors.red};
    width: 20%;
    text-decoration: none;
    border: none;
    padding: 10px;
    margin-top: 25px;
    cursor: pointer;

    &:hover{
        background: #333;
    }
`;

const Networks = styled.div`
    display: flex;
    flex-direction: column-reverse;
    align-items: center;
`;

const AboutMeDiv = styled.div`
    background: orange;
    height: 400px;
`;

const ExperienceDiv = styled.div`
    background: yellow;
    height: 400px;
`;

const ContactDiv = styled.div`
    background: blue;
    height: 400px;
`;



export { MainScreen, FirstScreen, ButtonAnimate, Menu, OptionMenu, Presentation, Networks,
            AboutMeDiv, ExperienceDiv, ContactDiv }
